export function quickSort(array) {
  const animations = [];
  let low = 0;
  let high = array.length - 1;
  quickSortHelper(array, low, high, animations);
  return animations;
}

function quickSortHelper(array, l, h, animations) {
  if (l < h) {
    let pi = partition(array, l, h, animations);
    quickSortHelper(array, l, pi - 1, animations);
    quickSortHelper(array, pi + 1, h, animations);
  }
  return array;
}

function partition(array, l, h, animations) {
  let pivot = array[h];

  let i = l - 1;

  let left = l;
  let right = h;

  for (let j = l; j <= h - 1; j++) {
    if (array[j] < pivot) {
      i++;
      swap(array, i, j);
    }
  }
  // let temp2 = array[i + 1];
  // array[i + 1] = array[h];
  // array[h] = temp2;
  swap(array, i + 1, h);
  return i + 1;
}

function swap(array, i, j) {
  let temp = array[i];
  array[i] = array[j];
  array[j] = temp;
}
