export const mergeSort = (array) => {
  //Find the middle point

  let middlePoint = Math.floor(array.length / 2);

  let temp = [];
  let temp2 = [];

  let sortedArray = [];

  for (let i = 0; i < middlePoint; i++) {
    temp[i] = array[i];
  }

  for (let j = middlePoint; j < array.length; j++) {
    temp2[j] = array[j];
  }

  console.log("This is the first half");
  console.log(temp);
  console.log("This is the second half");
  console.log(temp2);

  //Call mergeSort for the first half (0 - middle)

  mergeSortHelper();
};

function mergeSortHelper() {
  console.log("You merge me");
}
