export function bubbleSort(array) {
  let N = array.length;

  for (let i = 1; i < N; i++) {
    for (let j = 0; j < N - 1; j++) {
      if (array[j] > array[j + 1]) {
        swap(array, i, j);
      }
    }
  }

  return array;
}

function swap(array, i, j) {
  let temp = array[j];
  array[j] = array[j + 1];
  array[j + 1] = temp;
}
