export const mergeSort = (array) => {
  //Find the middle point

  if (array.length < 1) {
    return array;
  }

  const middlePoint = Math.floor(array.length / 2);
  const tempL = [];
  const tempR = [];

  let sortedArray = [];

  tempL = array.slice(0, middlePoint);
  tempR = array.slice(middlePoint);

  console.log("This is the left");
  console.log(tempL);
  console.log("This is the right");
  console.log(tempR);
};

function mergeSortHelper() {
  console.log("You merge me");
}
