export function quickSort(array) {
  const sortedArray = [];
  let low = 0;
  let high = array.length - 1;
  quickSortHelper(array, low, high);
  console.log("LOLOLLO");
  console.log("LOLOLLO1111");
}

function quickSortHelper(array, l, h) {
  console.log("LOLOLLO222");
  if (l < h) {
    console.log("LOOOP");
    let pi = partition(array, l, h);
    quickSortHelper(array, l, pi - 1);
    quickSortHelper(array, pi + 1, h);
  }

  console.log("RIP LOOP");
  return array;
}

function partition(array, l, h) {
  let pivot = array[h];

  let i = l - 1;

  for (let j = l; j <= h - 1; j++) {
    if (array[j] < pivot) {
      i++;
      //swap array[i] and array[j]
      let temp = array[i];
      array[i] = array[j];
      array[j] = temp;
    }
  }

  //swap array[i + 1] and array[high])
  let temp2 = array[i + 1];
  array[i + 1] = array[h];
  array[h] = temp2;

  console.log("LOLOLLO");
  return i + 1;
}
