export const mergeSort = (array) => {
  if (array.length <= 1) {
    return array;
  }

  const middlePoint = Math.floor(array.length / 2);
  const tempL = array.slice(0, middlePoint);
  const tempR = array.slice(middlePoint);

  return mergeSortHelper(mergeSort(tempL), mergeSort(tempR));
};

function mergeSortHelper(left, right) {
  let sortArray = [];

  let i = 0;
  let j = 0;
  let k = 0;

  while (i < left.length && j < right.length) {
    if (left[i] <= right[j]) {
      sortArray.push(left[i]);

      i++;
    } else {
      sortArray.push(right[j]);

      j++;
    }
    k++;
  }

  return sortArray.concat(left.slice(i)).concat(right.slice(j));
}
