export const mergeSort = (array, animation = []) => {
  if (array.length <= 1) {
    return array;
  }

  const middlePoint = Math.floor(array.length / 2);
  const tempL = array.slice(0, middlePoint);
  const tempR = array.slice(middlePoint);

  return mergeSortHelper(mergeSort(tempL), mergeSort(tempR), animations);
};

function mergeSortHelper(left, right, animations) {
  let sortArray = [];

  let i = 0;
  let j = 0;
  let k = 0;

  while (i < left.length && j < right.length) {
    const animation = [];
    animation.comparison = [i, j];
    if (left[i] <= right[j]) {
      animation.swap = [k, i];
      sortArray.push(left[i]);
      i++;
    } else {
      sortArray.push(right[j]);
      j++;
    }
    k++;
  }

  return sortArray.concat(left.slice(i)).concat(right.slice(j));
}
