export function selectionSort(array) {
  for (let j = 0; j < array.length - 1; j++) {
    let iMin = j;

    for (let i = j + 1; i < array.length; i++) {
      if (array[i] < array[iMin]) {
        iMin = i;
      }

      if (iMin != j) {
        let temp = array[j];
        array[j] = array[iMin];
        array[iMin] = temp;
      }
    }
  }

  return array;
}
