export function quickSort(array) {
  const animations = [];
  let low = 0;
  let high = array.length - 1;
  quickSortHelper(array, low, high);
}

function quickSortHelper(array, l, h, animations) {
  if (l < h) {
    let pi = partition(array, l, h, animations);
    quickSortHelper(array, l, pi - 1, animations);
    quickSortHelper(array, pi + 1, h, animations);
  }
  return array;
}

function partition(array, l, h, animations) {
  let pivot = array[h];

  console.log("This pivot is " + pivot);

  let i = l - 1;

  console.log("The i is " + i);
  console.log("The h is " + h);

  for (let j = l; j <= h - 1; j++) {
    if (array[j] < pivot) {
      i++;
      let temp = array[i];
      array[i] = array[j];
      array[j] = temp;
      animations.push([array[i], array[j]]);
    }
  }

  let temp2 = array[i + 1];
  console.log("array[i + 1] is " + temp2);
  array[i + 1] = array[h];
  console.log("array[i + 1] swap with array[h] " + array[i + 1]);
  array[h] = temp2;
  console.log("array[h] is " + temp2);
  return i + 1;
}
