export const mergeSort = (array) => {
  const animations = [];
  if (array.length <= 1) {
    return array;
  }

  const middlePoint = Math.floor(array.length / 2);
  const tempL = array.slice(0, middlePoint);
  const tempR = array.slice(middlePoint);

  return mergeSortHelper(
    mergeSort(tempL),
    mergeSort(tempR),
    animations,
    middlePoint
  );
};

function mergeSortHelper(left, right, animations, middleIdx) {
  let sortArray = [];

  let i = 0;
  let j = 0;
  let k = 0;

  while (i < left.length && j < right.length) {
    animations.push([i, j]);
    animations.push([i, j]);
    if (left[i] <= right[j]) {
      animations.push([k, left[i]]);
      sortArray.push(left[i]);
      i++;
    } else {
      animations.push([k, right[j]]);
      sortArray.push(right[j]);
      j++;
    }
    k++;
  }

  //   return sortArray.concat(left.slice(i)).concat(right.slice(j));
  return animations;
}
