export const mergeSort = (array) => {
  //Find the middle point

  let middlePoint = Math.floor(array.length / 2);

  let temp = [];
  let temp2 = [];

  let sortedArray = [];

  for (let i = 0; i < middlePoint; i++) {
    temp.push(array[i]);
  }

  for (let j = middlePoint; j < array.length; j++) {
    temp2.push(array[j]);
  }

  mergeSort(temp);
  mergeSort(temp2);
};

function mergeSortHelper() {
  console.log("You merge me");
}
