export function quickSort(array) {
  let low = array(0);
  let high = array(array.length - 1);
  quickSortHelper(array, l, h);
}

function quickSortHelper(array, l, h) {
  if (l < h) {
    pi = partition(array, l, h);

    quickSortHelper(array, l, pi - 1);
    quickSortHelper(array, pi + 1, h);
  }
}

function partition(array, l, h) {
  let pivot = array[h];

  let i = l - 1;

  for (j = l; j <= h - 1; j++) {
    if (array[j] < pivot) {
      i++;
      //swap array[i] and array[j]
      let temp = array[i];
      array[i] = array[j];
      array[j] = temp;
    }
  }

  //swap array[i + 1] and array[high])
  let temp2 = array[i + 1];
  array[i + 1] = array[h];
  array[h] = temp;

  return i + 1;
}
